package week2.day1;

public class learnforloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
        //print the odd numbers between 10 & 20.
		System.out.println("printing the for loop");

		for ( int i=10; i<20;i++) {
		if ((i%2==1)) {
		System.out.println(i);
		
		}
       
	}
		//reversing the loop
		System.out.println("reversing the for loop");
				for ( int i=20; i>=10;i--) {
					if ((i%2==1)) {
					System.out.println(i);
	}
		}
	}
}
	
